import { createContext, useContext, useState, useReducer, useEffect} from "react";

export const initialApiState = ({})


export const ContextGlobal = createContext(undefined);


//declarar estados iniciales


//aplicacion reducer

  const apiReducer = (state, action) => {
    switch(action.type){
        case 'GET_DENT':
            return action.payload
        default:
            throw new Error()
    }
  }

export const ContextProvider = ({ children }) => {
  //Aqui deberan implementar la logica propia del Context, utilizando el hook useMemo
  //estados reducer
  const [apiState, apiDispatch] = useReducer(apiReducer, initialState)


  const url = 'https://jsonplaceholder.typicode.com/users'

    useEffect(() => {
        fetch(url)
        .then(res => res.json())
        .then(data => apiDispatch({type: 'GET_DENT', payload: data}))
    }, [])

  return (
    <ContextGlobal.Provider value={{initialState, apiState}}>
      {children}
    </ContextGlobal.Provider>
  );
};
export const useContextGlobal = () => useContext(ContextGlobal)
